package com.yash.multithreading;

public class Quote {

	public static void main(String[] args) throws InterruptedException {
		// TODO Auto-generated method stub
		System.out.println("Yerterday was History");
		Thread.sleep(5000);
		System.out.println("Tomorrow is Mystery");
		Thread.sleep(5000);
		System.out.println("But Today is a Gift");
		Thread.sleep(5000);
		System.out.println("Thst's why it is called Present :)");
	}

}
